library(shiny)

# UI Definition
ui <- fluidPage(
  tags$head(
    tags$style(HTML("
      .completed { 
        background-color: #0077bb !important; 
        color: #ffffff !important;
        border: 3px solid #004477 !important;
        font-weight: bold !important;
      }
      .unlocked { 
        background-color: #ee7733 !important; 
        color: #000000 !important;
        border: 3px solid #cc5511 !important;
        font-weight: bold !important;
      }
      .locked { 
        background-color: #cccccc !important; 
        color: #666666 !important;
        border: 3px solid #999999 !important;
      }
      .room-btn { margin-bottom: 10px; }
      .game-container { max-width: 1200px; margin: 0 auto; }
      .story-box {
        background-color: #f0f4f8;
        border-left: 5px solid #0077bb;
        padding: 15px;
        margin: 15px 0;
        font-style: italic;
      }
      .email-box {
        background-color: #ffffff;
        border: 2px solid #ccc;
        padding: 15px;
        margin: 15px 0;
        font-family: 'Courier New', monospace;
        font-size: 14px;
      }
      .email-header {
        border-bottom: 1px solid #ccc;
        padding-bottom: 10px;
        margin-bottom: 10px;
      }
    "))
  ),

  div(class = "game-container",
      titlePanel("ðŸ”¬ Open Science Escape Room"),

      # Progress tracker at top
      fluidRow(
        column(12,
               wellPanel(
                 uiOutput("progress_display"),
                 uiOutput("timer_display")
               )
        )
      ),

      # Main game area
      fluidRow(
        column(3,
               wellPanel(
                 h4("ðŸšª Challenges"),
                 uiOutput("room_selector")
               )
        ),
        column(9,
               wellPanel(
                 uiOutput("puzzle_area")
               )
        )
      ),

      # Footer
      fluidRow(
        column(12,
               hr(),
               p(style = "text-align: center; color: #6c757d;",
                 HTML('Open Science Escape Room | Built with Shiny + shinylive | <a href="https://tvpollet.github.io/disclaimer/" target="_blank">Disclaimer</a>')
               )
        )
      )
  )
)

# Server Logic
server <- function(input, output, session) {

  # Game state - tracks completion and unlocks
  game_state <- reactiveValues(
    start_time = as.numeric(Sys.time()),
    time_limit = 15 * 60, # 15 minutes in seconds
    current_room = "lobby",
    hints_used = 0,
    lives = 3,
    game_over = FALSE,
    puzzles = list(
      preregistration = list(unlocked = TRUE, completed = FALSE, name = "Challenge 1: Preregistration"),
      p_hacking = list(unlocked = FALSE, completed = FALSE, name = "Challenge 2: QRPs"),
      power_analysis = list(unlocked = FALSE, completed = FALSE, name = "Challenge 3: Power Analysis"),
      open_data = list(unlocked = FALSE, completed = FALSE, name = "Challenge 4: FAIR Data"),
      fraudster_hall = list(unlocked = FALSE, completed = FALSE, name = "Challenge 5: Hall of Fame"),
      final_challenge = list(unlocked = FALSE, completed = FALSE, name = "Final Challenge")
    )
  )

  # Auto-refresh timer every second
  timer <- reactiveTimer(1000)

  # Unlock logic - sequential progression
  observeEvent(game_state$puzzles$preregistration$completed, {
    game_state$puzzles$p_hacking$unlocked <- TRUE
  })

  observeEvent(game_state$puzzles$p_hacking$completed, {
    game_state$puzzles$power_analysis$unlocked <- TRUE
  })

  observeEvent(game_state$puzzles$power_analysis$completed, {
    game_state$puzzles$open_data$unlocked <- TRUE
  })

  observeEvent(game_state$puzzles$open_data$completed, {
    game_state$puzzles$fraudster_hall$unlocked <- TRUE
  })

  observeEvent(game_state$puzzles$fraudster_hall$completed, {
    # Only unlock final challenge when ALL other puzzles are completed
    if (game_state$puzzles$preregistration$completed &&
        game_state$puzzles$p_hacking$completed &&
        game_state$puzzles$power_analysis$completed &&
        game_state$puzzles$open_data$completed &&
        game_state$puzzles$fraudster_hall$completed) {
      game_state$puzzles$final_challenge$unlocked <- TRUE
    }
  })

  # Progress display
  output$progress_display <- renderUI({
    completed <- sum(sapply(game_state$puzzles, function(x) x$completed))
    total <- length(game_state$puzzles)

    div(
      h4(sprintf("Progress: %d/%d challenges completed | ðŸ’¡ Hints: %d/3 used", completed, total, game_state$hints_used)),
      HTML(paste(rep("âœ…", completed), rep("ðŸ”’", total - completed), collapse = " ")),
      p(style = "margin-top: 10px; font-size: 18px;",
        sprintf("â¤ï¸ Lives remaining: %s", paste(rep("â¤ï¸", game_state$lives), collapse = " ")))
    )
  })

  # Timer display
  output$timer_display <- renderUI({
    timer()  # Trigger reactive update
    elapsed <- as.numeric(Sys.time()) - game_state$start_time
    remaining <- game_state$time_limit - elapsed

    if (remaining <= 0 && !all(sapply(game_state$puzzles, function(x) x$completed))) {
      # Time's up and not all completed
      game_state$game_over <- TRUE
      game_state$current_room <- "lobby"
      time_text <- "â° TIME'S UP!"
      time_color <- "red"
    } else if (remaining <= 120) {
      # Less than 2 minutes - urgent
      mins <- floor(remaining / 60)
      secs <- floor(remaining %% 60)
      time_text <- sprintf("â° Time remaining: %d:%02d (HURRY!)", mins, secs)
      time_color <- "red"
    } else if (remaining <= 300) {
      # Less than 5 minutes - warning
      mins <- floor(remaining / 60)
      secs <- floor(remaining %% 60)
      time_text <- sprintf("â° Time remaining: %d:%02d", mins, secs)
      time_color <- "orange"
    } else {
      # Plenty of time
      mins <- floor(remaining / 60)
      secs <- floor(remaining %% 60)
      time_text <- sprintf("â° Time remaining: %d:%02d", mins, secs)
      time_color <- "black"
    }

    p(style = sprintf("color: %s; font-weight: bold;", time_color), time_text)
  })

  # Room selector buttons
  output$room_selector <- renderUI({
    # Build list of visible rooms - only those that are unlocked OR completed
    buttons <- list()

    for (room_name in names(game_state$puzzles)) {
      puzzle <- game_state$puzzles[[room_name]]

      # Only show this room if it's unlocked OR completed
      if (puzzle$unlocked || puzzle$completed) {
        if (puzzle$completed) {
          btn_class <- "btn-success room-btn completed"
          icon_status <- "âœ…"
        } else if (puzzle$unlocked) {
          btn_class <- "btn-warning room-btn unlocked"
          icon_status <- "ðŸ”“"
        } else {
          btn_class <- "btn-secondary room-btn locked"
          icon_status <- "ðŸ”’"
        }

        buttons[[length(buttons) + 1]] <- actionButton(
          inputId = paste0("room_", room_name),
          label = paste(icon_status, puzzle$name),
          width = "100%",
          class = btn_class
        )
      }
    }

    # Add reset button
    buttons[[length(buttons) + 1]] <- hr()
    buttons[[length(buttons) + 1]] <- actionButton("reset_game_btn", "ðŸ”„ Start Over", 
                                                   width = "100%", class = "btn-danger")

    do.call(tagList, buttons)
  })

  # Room button observers
  observe({
    lapply(names(game_state$puzzles), function(room) {
      observeEvent(input[[paste0("room_", room)]], {
        if (game_state$puzzles[[room]]$unlocked) {
          game_state$current_room <- room
        }
      })
    })
  })

  # Main puzzle area
  output$puzzle_area <- renderUI({
    # Check for time up
    elapsed <- as.numeric(Sys.time()) - game_state$start_time
    remaining <- game_state$time_limit - elapsed

    if (remaining <= 0 && !all(sapply(game_state$puzzles, function(x) x$completed))) {
      game_state$game_over <- TRUE
      return(div(
        h2("â° Time's Up!"),

        div(class = "email-box",
            div(class = "email-header",
                strong("From:"), " Prof. Brian Stapel", br(),
                strong("To:"), " You", br(),
                strong("Subject:"), " RE: Ethics Application - OUT OF TIME", br(),
                strong("Time:"), " 2:00 PM (15 minutes expired)"
            ),
            p("Dear Student,"),
            p("I'm afraid our meeting time has arrived and you haven't completed all the challenges yet."),
            p("Your ethics application cannot be approved without demonstrating competency in all six areas of open science practice. We'll need to reschedule, which will delay your data collection timeline."),
            p("Take some time to review the material and try again when you're better prepared. Remember: speed matters, but accuracy matters more."),
            p("Best,", br(), "Prof. Stapel")
        ),

        hr(),
        actionButton("reset_game_after_loss", "ðŸ”„ Try Again", class = "btn-primary btn-lg")
      ))
    }

    # Check for game over (lives)
    if (game_state$game_over) {
      return(div(
        h2("ðŸ’€ Ethics Application Rejected"),

        div(class = "email-box",
            div(class = "email-header",
                strong("From:"), " Prof. Brian Stapel", br(),
                strong("To:"), " You", br(),
                strong("Subject:"), " RE: Ethics Application - NEEDS REVISION", br(),
                strong("Time:"), " 2:00 PM"
            ),
            p("Dear Student,"),
            p("I'm afraid we've run out of time. You've used all three attempts and haven't completed all the necessary challenges."),
            p("Your ethics application cannot be approved without demonstrating competency in all six areas of open science practice. This isn't just bureaucracy - these are essential skills for conducting ethical, reproducible research."),
            p("The good news: You can retake these challenges. Many students need multiple attempts to fully grasp these concepts. Take some time to review the BPS Code of Human Research Ethics and the material from your Research Methods module."),
            p("When you're ready, come back and try again. Your dissertation timeline will be delayed, but it's better to get this right than to rush and make mistakes."),
            p("Best,", br(), "Prof. Stapel")
        ),

        div(class = "story-box",
            p(strong("Remember:"), " In real research, there are no 'lives' or second chances once you've collected data with questionable practices. That's why we get it right from the start!")
        ),

        hr(),
        actionButton("reset_game_after_loss", "ðŸ”„ Try Again (Restart Challenges)", class = "btn-primary btn-lg")
      ))
    }

    switch(game_state$current_room,
           "preregistration" = preregistration_ui(),
           "p_hacking" = p_hacking_ui(),
           "power_analysis" = power_analysis_ui(),
           "open_data" = open_data_ui(),
           "fraudster_hall" = fraudster_hall_ui(),
           "final_challenge" = final_challenge_ui(),
           # Default lobby
           div(
             h2("ðŸŽ“ The Dissertation Dilemma"),

             div(class = "email-box",
                 div(class = "email-header",
                     strong("From:"), " Prof. Brian Stapel (Dissertation Supervisor)", br(),
                     strong("To:"), " You (Final Year Psychology Student)", br(),
                     strong("Subject:"), " URGENT: Your Dissertation Project", br(),
                     strong("Date:"), " Today, 9:00 AM"
                 ),
                 p("Dear Student,"),
                 p("I've just reviewed your dissertation proposal for your final year project, and I'm afraid we have a serious problem."),
                 p("Your literature review is excellent, and your research question is solid. However, your proposed methodology doesn't meet the open science standards required by our department and the Belgian Psychological Society. Without demonstrating proper understanding of these principles, I cannot approve your ethics application, and you won't be able to collect data."),
                 p("I've prepared six challenges that cover the essential open science practices you'll need for your project. ", strong("You have exactly 15 minutes to complete them all"), " before our 2 PM meeting, or we'll need to significantly delay your data collection - which would put your graduation timeline at risk."),
                 p("I know this seems daunting, but these are skills every psychology researcher needs. Consider this your crash course in responsible research practices. You have three attempts at each challenge (think of them as 'lives'), so use them wisely."),
                 p("The first challenge is ready for you now. Once you complete it, the next will unlock."),
                 p("Good luck! I believe in you, but the clock is ticking..."),
                 p("Best,", br(), "Prof. Stapel")
             ),

             div(class = "story-box",
                 p(strong("Your situation:"), " It's 9:00 AM. You have ", strong("15 minutes"), " to prove you understand open science principles well enough to conduct ethical, reproducible research for your dissertation. Your degree depends on it.")
             ),

             hr(),
             h4("â° The Challenge:"),
             tags$ul(
               tags$li("Complete six open science challenges in 15 minutes"),
               tags$li("Each covers a critical aspect of your dissertation research"),
               tags$li("You have 3 attempts (lives) - use them carefully!"),
               tags$li("You can use up to 3 hints across all challenges"),
               tags$li("New challenges unlock as you progress"),
               tags$li("Complete all challenges before time runs out to approve your ethics application")
             ),
             hr(),
             p(strong("Ready to save your dissertation? Click on the first challenge to begin! The timer starts NOW! â°"))
           )
    )
  })

  # PUZZLE 1: Preregistration
  preregistration_ui <- function() {
    tagList(
      h3("ðŸ“‹ Challenge 1: Preregistration Requirements"),

      div(class = "story-box",
          p(strong("Prof. Stapel's note:"), " \"Before you collect any data, you need to preregister your study. This prevents questionable research practices and shows you're committed to transparency. Different types of research require different registration platforms - let's see if you know which is which.\"")
      ),

      p("Your dissertation involves reviewing different types of psychological research. Match each scenario to the appropriate preregistration platform:"),
      hr(),
      fluidRow(
        column(6,
               h4("Research Scenarios:"),
               div(style = "padding: 10px; margin: 5px; background-color: #e9ecef; border-radius: 5px;",
                   strong("Scenario 1:"), " A clinical trial testing a new CBT intervention for anxiety in adolescents"
               ),
               div(style = "padding: 10px; margin: 5px; background-color: #e9ecef; border-radius: 5px;",
                   strong("Scenario 2:"), " Exploratory analysis of the UK Household Longitudinal Study (Understanding Society) data"
               ),
               div(style = "padding: 10px; margin: 5px; background-color: #e9ecef; border-radius: 5px;",
                   strong("Scenario 3:"), " A direct replication of Dweck's mindset intervention study"
               ),
               div(style = "padding: 10px; margin: 5px; background-color: #e9ecef; border-radius: 5px;",
                   strong("Scenario 4:"), " Your dissertation: A confirmatory study testing whether social media use predicts wellbeing in university students"
               ),
               div(style = "padding: 10px; margin: 5px; background-color: #e9ecef; border-radius: 5px;",
                   strong("Scenario 5:"), " A systematic review and meta-analysis on mindfulness interventions for depression"
               )
        ),
        column(6,
               h4("Your Answers:"),
               selectInput("match1", "Scenario 1 matches with:", 
                           choices = c("Choose..." = "", "AsPredicted", "ClinicalTrials.gov", "Secondary Data Preregistration", "Replication Recipe", "PROSPERO")),
               selectInput("match2", "Scenario 2 matches with:", 
                           choices = c("Choose..." = "", "AsPredicted", "ClinicalTrials.gov", "Secondary Data Preregistration", "Replication Recipe", "PROSPERO")),
               selectInput("match3", "Scenario 3 matches with:", 
                           choices = c("Choose..." = "", "AsPredicted", "ClinicalTrials.gov", "Secondary Data Preregistration", "Replication Recipe", "PROSPERO")),
               selectInput("match4", "Scenario 4 matches with:", 
                           choices = c("Choose..." = "", "AsPredicted", "ClinicalTrials.gov", "Secondary Data Preregistration", "Replication Recipe", "PROSPERO")),
               selectInput("match5", "Scenario 5 matches with:", 
                           choices = c("Choose..." = "", "AsPredicted", "ClinicalTrials.gov", "Secondary Data Preregistration", "Replication Recipe", "PROSPERO"))
        )
      ),
      hr(),
      actionButton("check_prereg", "âœ… Submit Answers", class = "btn-primary btn-lg"),
      actionButton("hint_prereg", "ðŸ’¡ Request Hint", class = "btn-info"),
      br(), br(),
      uiOutput("prereg_feedback")
    )
  }

  observeEvent(input$check_prereg, {
    correct <- c("ClinicalTrials.gov", "Secondary Data Preregistration", "Replication Recipe", "AsPredicted", "PROSPERO")
    answers <- c(input$match1, input$match2, input$match3, input$match4, input$match5)

    if (all(answers == correct)) {
      game_state$puzzles$preregistration$completed <- TRUE

      output$prereg_feedback <- renderUI({
        div(class = "alert alert-success",
            h4("âœ… Excellent work!"),
            p("Clinical trials â†’ ClinicalTrials.gov, secondary data analysis â†’ Secondary Data Preregistration, replication studies â†’ ", 
              HTML('<a href="https://doi.org/10.1016/j.jesp.2013.10.005" target="_blank">Replication Recipe</a>'),
              ", confirmatory studies â†’ AsPredicted, and systematic reviews â†’ PROSPERO."),
            div(class = "story-box",
                p(strong("Prof. Stapel:"), " \"Well done! You've got the basics of preregistration. For your own dissertation, you'll be using a pre-registration template such as AsPredicted since you have clear hypotheses to test. This will protect you from accusations of HARKing and p-hacking. Next challenge unlocked...\"")
            )
        )
      })
    } else {
      # Lose a life
      game_state$lives <- game_state$lives - 1

      if (game_state$lives <= 0) {
        game_state$game_over <- TRUE
        game_state$current_room <- "lobby"
      } else {
        num_correct <- sum(answers == correct)
        output$prereg_feedback <- renderUI({
          div(class = "alert alert-danger",
              h4(sprintf("âŒ Wrong! You lost a life. %d lives remaining.", game_state$lives)),
              p(sprintf("You got %d out of 5 correct. Think about which platform is specifically designed for each type of study.", num_correct))
          )
        })
      }
    }
  })

  observeEvent(input$hint_prereg, {
    if (game_state$hints_used < 3) {
      game_state$hints_used <- game_state$hints_used + 1
      showModal(modalDialog(
        title = "ðŸ’¡ Hint",
        "ClinicalTrials.gov is for clinical trials. PROSPERO is for systematic reviews. Secondary Data Preregistration is for analyzing existing datasets. Replication Recipe is specifically for replication studies. AsPredicted is for confirmatory hypotheses in new studies.",
        easyClose = TRUE,
        footer = modalButton("Close")
      ))
    } else {
      showModal(modalDialog(
        title = "No Hints Left",
        "You've used all 3 hints! Keep trying!",
        easyClose = TRUE,
        footer = modalButton("Close")
      ))
    }
  })

  # PUZZLE 2: P-Hacking - NOW WITH 8 OPTIONS (3 legitimate + 5 QRPs)
  p_hacking_ui <- function() {
    tagList(
      h3("ðŸ” Challenge 2: Spotting Questionable Research Practices"),

      div(class = "story-box",
          p(strong("Prof. Stapel's note:"), " \"Now you need to understand WHY preregistration matters. I'm showing you an analysis log from a student's dissertation. Your task: identify ONLY the questionable research practices. Not all decisions in research are problematic - distinguish between legitimate choices and QRPs.\"")
      ),

      p("Analyse this log carefully. Select ", strong("ONLY"), " the questionable research practices (QRPs):"),
      hr(),
      wellPanel(
        h4("Analysis Log - Social Media and Wellbeing Study"),
        p(em("(From a previous student - submitted but rejected)")),
        tags$pre(style = "background-color: #f8f9fa; padding: 15px;",
                 "Step 1: Pre-registered study on OSF. Specified N=100, primary DV: life 
      satisfaction, secondary DV: depression. Outlier criteria: >3 SD.

Step 2: Collected n=50, ran t-test on life satisfaction. p=.08
      Not significant... let's keep collecting until significant.

Step 3: Added 20 more participants. Now p=.045! Significant! âœ“
      Stopped data collection early at n=70.

Step 4: Ran pre-registered secondary analysis on depression scores.
      Depression: p=.03. Good!

Step 5: Checked for outliers using pre-specified >3 SD criteria.
      Found 2 outliers but p still .03. Changed criterion to >2.5 SD,
      removed 5 outliers. Now p=.008! Much better.

Step 6: Added age as covariate (NOT pre-registered). Now p=.002!
      Much better results.

Step 7: Writing up - focusing on depression as 'primary outcome' since
      it showed strongest effect. Life satisfaction now secondary."
        )
      ),
      checkboxGroupInput("qrp_identified",
                         "Select ONLY the questionable research practices (NOT legitimate practices):",
                         choices = c(
                           "Pre-registering the study on OSF" = "prereg",
                           "Optional stopping (collecting until significance, then stopping early)" = "optional_stop",
                           "Running the pre-registered secondary analysis on depression" = "secondary",
                           "Selective reporting (switching primary and secondary outcomes)" = "selective_dv",
                           "Having pre-specified outlier removal criteria (>3 SD)" = "outlier_criteria",
                           "Changing outlier criterion post-hoc to get significant results (3 SD to 2.5 SD)" = "outlier",
                           "Adding a non-preregistered covariate" = "covariates",
                           "HARKing (presenting secondary outcome as primary)" = "harking"
                         )
      ),
      hr(),
      actionButton("check_phack", "âœ… Submit Analysis", class = "btn-primary btn-lg"),
      actionButton("hint_phack", "ðŸ’¡ Request Hint", class = "btn-info"),
      br(), br(),
      uiOutput("phack_feedback")
    )
  }

  observeEvent(input$check_phack, {
    correct_qrps <- c("optional_stop", "selective_dv", "outlier", "covariates", "harking")
    identified <- input$qrp_identified

    all_correct <- length(setdiff(correct_qrps, identified)) == 0 && 
      length(setdiff(identified, correct_qrps)) == 0

    if (all_correct) {
      game_state$puzzles$p_hacking$completed <- TRUE

      output$phack_feedback <- renderUI({
        div(class = "alert alert-success",
            h4("âœ… Excellent detective work!"),
            p("You correctly identified the 5 QRPs:"),
            tags$ol(
              tags$li(strong("Optional stopping"), " - stopping based on results, not pre-specified N"),
              tags$li(strong("Selective reporting"), " - switching primary/secondary outcomes"),
              tags$li(strong("Post-hoc outlier criterion change"), " - changing from 3 SD to 2.5 SD to get significance"),
              tags$li(strong("Adding non-preregistered covariates"), " - age wasn't pre-registered"),
              tags$li(strong("HARKing"), " - presenting depression as primary when it was secondary")
            ),
            p(strong("Good practices you correctly identified as legitimate:")),
            tags$ul(
              tags$li("Pre-registering the study âœ“"),
              tags$li("Running pre-registered analyses âœ“"),
              tags$li("Having pre-specified criteria âœ“")
            ),
            div(class = "story-box",
                p(strong("Prof. Stapel:"), " \"Perfect! The key is whether decisions were made before or after seeing data. Pre-registration makes this transparent. That student violated their own pre-registration multiple times. Don't let this happen to you!\"")
            )
        )
      })
    } else {
      # Lose a life
      game_state$lives <- game_state$lives - 1

      if (game_state$lives <= 0) {
        game_state$game_over <- TRUE
        game_state$current_room <- "lobby"
      } else {
        num_correct <- length(intersect(correct_qrps, identified))
        num_false_positives <- length(setdiff(identified, correct_qrps))

        output$phack_feedback <- renderUI({
          div(class = "alert alert-danger",
              h4(sprintf("âŒ Wrong! You lost a life. %d lives remaining.", game_state$lives)),
              if (num_false_positives > 0) {
                p(sprintf("You identified %d QRPs correctly but also selected %d legitimate practice(s). Not all research decisions are questionable!", num_correct, num_false_positives))
              } else {
                p(sprintf("You identified %d QRPs correctly. There are 5 total. Think about what was pre-specified vs. post-hoc.", num_correct))
              }
          )
        })
      }
    }
  })

  observeEvent(input$hint_phack, {
    if (game_state$hints_used < 3) {
      game_state$hints_used <- game_state$hints_used + 1
      showModal(modalDialog(
        title = "ðŸ’¡ Hint",
        "Key question: was each decision made BEFORE or AFTER seeing data? Pre-registering = good. Running pre-registered analyses = good. Using pre-specified criteria = good. But deviating from pre-registration (stopping early, switching outcomes, changing criteria to get significance, adding unplanned analyses) = QRP. Look for 5 violations.",
        easyClose = TRUE,
        footer = modalButton("Close")
      ))
    } else {
      showModal(modalDialog(
        title = "No Hints Left",
        "You've used all 3 hints! Keep trying!",
        easyClose = TRUE,
        footer = modalButton("Close")
      ))
    }
  })

  # PUZZLE 3: Power Analysis
  power_analysis_ui <- function() {
    tagList(
      h3("ðŸ“Š Challenge 3: Statistical Power and Sample Size"),

      div(class = "story-box",
          p(strong("Prof. Stapel's note:"), " \"Right, now for your own dissertation. Before the ethics committee will approve your study, you need to justify your sample size. This isn't just picking a number that 'sounds good' - you need a proper power analysis. Let's see if you can work this out...\"")
      ),

      p("For YOUR dissertation study comparing two groups of university students:"),
      hr(),
      wellPanel(
        h4("Your Study Parameters:"),
        tags$ul(
          tags$li("Design: Between-subjects experiment (two independent groups, balanced design)"),
          tags$li("Allocation ratio: 1:1 (equal group sizes)"),
          tags$li("Expected effect size: Cohen's d = 0.5 (medium effect, based on previous literature)"),
          tags$li("Desired statistical power: 0.80 (80% - standard in psychology)"),
          tags$li("Significance level: Î± = 0.05 (two-tailed test)"),
          tags$li("Statistical test: Independent samples t-test")
        ),
        p(em("Note: The ethics committee requires 80% power as minimum for approval."))
      ),
      p(strong("What is the minimum TOTAL sample size needed (both groups combined)?")),
      p("Remember: This is the number you'll need to justify in your ethics application."),
